mapper.select:
select ${key}, adcode from device where 1 = 1
and adcode like concat(adcode, '%')
and town=#{town}

select DATE_FORMAT(convert_tz(`date`, @@session.time_zone,'+08:00'), '%m') `key`, sum(num) `value` from device_maintenance left join device on device.id = device_maintenance.device_id where DATE_FORMAT(convert_tz(`date`, @@session.time_zone,'+08:00'), '%Y') = #{year}
and device.adcode like concat(#{adcode}, '%')
and device.town = #{town}
group by DATE_FORMAT(convert_tz(`date`, @@session.time_zone,'+08:00'), '%m')

select sum(num) beetlesNum,sum(other_num) otherNum,((sum(num) + sum(other_num))/count(device_Id)) avager,CustomTown
from device_maintenance t1 left join device t2 on t1.device_id = t2.id where CustomProject = #{customProject}
and DATE(`date`) >= #{startDate} and #{endDate} >=DATE(`date`)
group by CustomTown;
