mapper.select:
select count(distinct device_id ) deviceCount, sum(num) beetleCount, device.city `name`, device.adcode code from device_maintenance left join device on device_maintenance.device_id = device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and device.city is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device.city

select count(distinct device_id) deviceCount, sum(num) beetleCount, device_maintenance.area `name`, device_maintenance.adcode code from device_maintenance where device_maintenance.adcode like concat(#{adcode}, '%') and isactive = '0'
and device_maintenance.area is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device_maintenance.area

select count(distinct device_id) deviceCount, sum(num) beetleCount, device_maintenance.town `name`, device_maintenance.adcode code from device_maintenance where device_maintenance.adcode like concat(#{adcode}, '%') and isactive = '0'
and device_maintenance.town is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device_maintenance.town

select count(distinct device_id) deviceCount, sum(num) beetleCount, device_maintenance.manager `name`, device_maintenance.adcode code from device_maintenance where device_maintenance.adcode like concat(#{adcode}, '%') and isactive = '0'
and device_maintenance.manager is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device_maintenance.manager

select count(distinct device_id) deviceCount, sum(num) beetleCount, username `name`, username code from device_maintenance where device_maintenance.adcode like concat(#{adcode}, '%') and isactive = '0'
and username is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by username

select count(distinct device_id) `count`, sum(num) `sum`
from device_maintenance

where device_maintenance.adcode like concat(#{adcode}, '%')
      and isactive = '0'



    and `date` >= #{startDate}




    and `date` <= #{endDate}

select count(distinct device_maintenance.device_id) `count`, sum(num) `sum` from device_maintenance
left join device on device.id = device_maintenance.device_id
left join user on device_maintenance.username = user.username
where parent = #{adcode}
      and isactive = '0'


    and `date` >= #{startDate}


    and `date` <= #{endDate}

select count(distinct device_id) deviceCount, sum(num) beetleCount, device_maintenance.username `name`, device_maintenance.username code
from device_maintenance
left join device on device_maintenance.device_id = device.id
left join user on device_maintenance.username = user.username
where parent = #{manager}
      and isactive = '0'


    and `date` >= #{startDate}



    and `date` <= #{endDate}

group by device_maintenance.username

select count(distinct device_id) deviceNum,sum(num) beetleNum,sum(other_num) otherNum,remark from device_maintenance
left join device on device.id = device_id where manager like concat('%',#{username},'%')

and device_maintenance.${colName} = #{searchText}


    and `date` >= #{startDate}


    and `date` <= #{endDate}

group by device_maintenance.${colName}
