mapper.select:
SELECT COUNT(Device_DeadTrees_maintance.id) FROM Device_DeadTrees_maintance
left join device on device.id = Device_DeadTrees_maintance.device_Id WHERE username = #{username}

  and t1.submit_date >= #{startDate}


  and t1.submit_date <= #{endDate}


  and ${colName} = #{searchText}

SELECT * FROM  Device_DeadTrees_maintance t1 LEFT JOIN device t2 ON t1.device_Id = t2.id
WHERE t2.adcode
LIKE concat(#{adcode}, '%')

  and t1.submit_date >= #{startDate}


  and t1.submit_date <= #{endDate}


  and ${colName} = #{searchText}

LIMIT #{num1},#{num2}

SELECT * FROM Device_DeadTrees_maintance t1 LEFT JOIN  device t2 ON t1.device_Id = t2.id
WHERE t2.adcode LIKE concat(#{adcode},'%')
LIMIT #{num1},#{num2}
