mapper.update:
update Device_Injection_maintance
set reported = 1
where id = #{id}

mapper.select:
select count(distinct device_id) deviceCount, sum(injection_num) injectNum, device.town `name`, device.adcode code from Device_Injection_maintance left join device on Device_Injection_maintance.device_Id = device.id where adcode like concat(#{adcode}, '%') and device.town is not null and device.project = '注干剂监测'
and submit_Date >= #{startDate}
and submit_Date <= #{endDate}
group by device.town

select Device_Injection_maintance.id, Device_Injection_maintance.device_Id, Device_Injection_maintance.batch, sum(Device_Injection_maintance.injection_num) injection_num, Device_Injection_maintance.Worker, user.name, Device_Injection_maintance.submit_date, Device_Injection_maintance.longitude, Device_Injection_maintance.latitude, Device_Injection_maintance.altitude, Device_Injection_maintance.Pic, Device_Injection_maintance.Remarks, device.province, device.city, device.area, device.town, Device_Injection_maintance.work_content, Device_Injection_maintance.username, Device_Injection_maintance.WoodStatus from Device_Injection_maintance left join device on device.id = Device_Injection_maintance.device_Id left join user on Device_Injection_maintance.Worker = user.username where device_Id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username = #{condition} or device.province = #{condition} or device.city = #{condition} or device.area = #{condition} or device.town = #{condition} or Device_Injection_maintance.device_Id = #{condition} or device.manager=#{condition} )
and submit_date >= #{date}
and submit_date <= #{endDate}
group by device_Id,Device_Injection_maintance.Worker
order by submit_date desc

