select Device_Injection_maintance.id, Device_Injection_maintance.device_Id, Device_Injection_maintance.batch, Device_Injection_maintance.injection_num, Device_Injection_maintance.username, user.name, Device_Injection_maintance.submit_date, Device_Injection_maintance.longitude, Device_Injection_maintance.latitude, Device_Injection_maintance.altitude, Device_Injection_maintance.Pic, Device_Injection_maintance.scanId, Device_Injection_maintance.Remarks, device.province, device.city, device.area, device.town, Device_Injection_maintance.work_content, Device_Injection_maintance.reported from Device_Injection_maintance left join device on device.id = Device_Injection_maintance.device_Id left join user on Device_Injection_maintance.username = user.username where device_Id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or Device_Injection_maintance.device_Id like concat('%', #{condition}, '%')
)
and `batch` = #{batch}
and submit_date >= #{date}
and device.town like concat('%', #{searchtown}, '%')
and submit_date <= #{endDate}
and reported = #{reported}
order by submit_date desc

