mapper.select:
select count(distinct device_Id) deviceCount, sum(Release_Num) releaseNum, device.town `name`, device.adcode code from Device_NaturalEnemies_maintance left join device on Device_NaturalEnemies_maintance.device_Id = device.id where adcode like concat(#{adcode}, '%')
and device.town is not null
and submit_date >= #{startDate}
and submit_date <= #{endDate}
group by device.town

select Device_NaturalEnemies_maintance.id, Device_NaturalEnemies_maintance.device_Id, Device_NaturalEnemies_maintance.batch, sum(Device_NaturalEnemies_maintance.Release_Num) releaseNum, Device_NaturalEnemies_maintance.Worker, user.name, Device_NaturalEnemies_maintance.submit_date, Device_NaturalEnemies_maintance.longitude, Device_NaturalEnemies_maintance.latitude, Device_NaturalEnemies_maintance.altitude, Device_NaturalEnemies_maintance.Pic, Device_NaturalEnemies_maintance.PredatorsType, Device_NaturalEnemies_maintance.Remarks, Device_NaturalEnemies_maintance.region,
device.province, device.city, device.area, device.town, Device_NaturalEnemies_maintance.reported from Device_NaturalEnemies_maintance left join device on device.id = Device_NaturalEnemies_maintance.device_Id left join user on Device_NaturalEnemies_maintance.Worker = user.username where device_Id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username =#{condition} or device.province =#{condition} or device.city = #{condition} or device.area =#{condition} or device.town = #{condition} or Device_NaturalEnemies_maintance.device_Id = #{condition} )
and submit_date >= #{date}
and submit_date <= #{endDate}
and manager = #{manager}
group by device_Id,Device_NaturalEnemies_maintance.Worker order by submit_date desc

select Device_NaturalEnemies_maintance.id, Device_NaturalEnemies_maintance.device_Id, Device_NaturalEnemies_maintance.batch, sum(Device_NaturalEnemies_maintance.Release_Num) releaseNum, Device_NaturalEnemies_maintance.Worker, user.name, Device_NaturalEnemies_maintance.submit_date, Device_NaturalEnemies_maintance.longitude, Device_NaturalEnemies_maintance.latitude, Device_NaturalEnemies_maintance.altitude, Device_NaturalEnemies_maintance.Pic, Device_NaturalEnemies_maintance.PredatorsType, Device_NaturalEnemies_maintance.Remarks, device.province, device.city, device.area, device.town, Device_NaturalEnemies_maintance.reported from Device_NaturalEnemies_maintance left join device on device.id = Device_NaturalEnemies_maintance.device_Id left join user on Device_NaturalEnemies_maintance.Worker = user.username where device_Id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username =#{condition} or device.province =#{condition} or device.city = #{condition} or device.area =#{condition} or device.town = #{condition} or Device_NaturalEnemies_maintance.device_Id = #{condition} )
and submit_date >= #{date}
and submit_date <= #{endDate}
and manager = #{manager}
group by device_Id,Device_NaturalEnemies_maintance.Worker order by submit_date desc
